select e.patient_ide, e.encounter_ide, date_trunc('day',v.start_date) as Aufnahmedatum, date_trunc('year',p.birth_date) as Geburtsjahr, p.sex_cd as Geschlecht, f.nval_num as PLZ from "i2b2crcdata"."patient_dimension" as p join "i2b2crcdata"."visit_dimension" as v on p.patient_num = v.patient_num join "i2b2crcdata"."encounter_mapping" e on e.encounter_num = v.encounter_num left join "i2b2crcdata"."observation_fact" as f on (p.patient_num = f.patient_num) and (v.encounter_num = f.encounter_num) and (f.concept_cd = 'AKTIN:ZIPCODE') 